import React, { useState, useEffect } from "react";
import {
  Text,
  View,
  StyleSheet,
  TextInput,
  TouchableOpacity,
  Picker,
} from "react-native";
import COLORS from '../../consts/colors'
import Icon from 'react-native-vector-icons/MaterialIcons';

const Post = ({ navigation }) => {


  const [choosenValue, setChoosenValue] = useState(2);
  const [choosenIndex, setChoosenIndex] = useState(1);
  const [data, setData] = useState([]);


  const [product, setproduct] = useState({
    name: "",
    price: "",
    description: "",
    image: "",
    category_id: "",

  });



  const [loading, setLoading] = useState(false);

  useEffect(() => {
    //GET request
    fetch('https://jsonplaceholder.typicode.com/posts', {
      method: 'GET',
      //Request Type
    })
      .then((response) => response.json())
      //If response is in json then in success
      .then((responseJson) => {
        //Success
        //alert(JSON.stringify(responseJson));
        // console.log(responseJson);
        setData(responseJson);
      })
      //If response is not in json then in error
      .catch((error) => {
        //Error
        alert(JSON.stringify(error));
        console.error(error);
      });
  });

  const yyy = (itemValue, itemIndex) => {

    alert(itemValue);
    alert(itemIndex);
  };




  const onChangepersonId = (value) => {
    setproduct({ ...product, name: value });
  };

  const onChangefirstName = (value) => {
    setproduct({ ...product, firstName: value });
  };

  const onChangelastName = (value) => {
    setproduct({ ...product, lastName: value });
  };

  const onChangeaddressId = (value) => {
    setproduct({ ...product, addressId: value });
  };
   const onChangestreet = (value) => {
     setproduct({ ...product, street: value });
   };

  const saveData = () => {
    setLoading(true);
    var myHeaders = new Headers();


    myHeaders.append("Content-Type", "application/json");

    fetch("https://myfirstapi.azurewebsites.net/api/person/create", {
      method: "POST",
      headers: myHeaders,
      body: JSON.stringify({
        personId: person.personId,
        firstName: person.firstName,
        lastName: person.lastName,
        address:address,
      }),
    })
      .then((response) => {
        setLoading(false);
        response.text();
      })
      .then((result) => console.log(result))
      .catch((error) => console.log(error));
  };

  return (
    <View style={styles.container}>
      <View>
        <Icon name="arrow-back" size={28} onPress={() => navigation.goBack()} />

        <Text style={{ fontSize: 25, fontWeight: "bold" }}>Welcome to</Text>
        <Text
          style={{ fontSize: 38, color: COLORS.green, fontWeight: "bold" }}
        >
          Yazan And Hamzah Pizza
        </Text>
      </View>
      <TextInput
        placeholder={"PersonID"}
        onChangeText={(value) => onChangepersonId(value)}
        style={styles.input}
      />
      <TextInput
        placeholder={"Firstname"}
        onChangeText={(value) => onChangefirstName(value)}
        style={styles.input}
      />
      <TextInput
        placeholder={"LastName"}
        onChangeText={(value) => onChangelastName(value)}
        style={styles.input}
      />
      <TextInput
        placeholder={"addresID"}
        onChangeText={(value) => onChangeaddressId(value)}
        style={styles.input}
      />
      <Picker
        selectedValue={choosenValue}
        onValueChange={(itemValue, itemIndex) => {
          yyy(itemValue, itemIndex);
          setChoosenValue(itemValue);
          setChoosenIndex(itemIndex);
        }}>
        {
          data.map(item => {
            return <Picker.Item value={item.id} label={item.title} />
          })
        }
      </Picker>

      <TextInput
        placeholder={"street"}
        onChangeText={(value) => onChangestreet(value)}
        style={styles.input}
      />
      <TextInput
        placeholder={"Postalcode"}
        onChangeText={(value) => onChangepostalCode(value)}
        style={styles.input}
      />
      <TextInput
        placeholder={"City"}
        onChangeText={(value) => onChangecity(value)}
        style={styles.input}
      />

      <TouchableOpacity onPress={saveData} >
        <View style={{ backgroundColor: "blue", padding: 10 }}>
          <Text style={{ color: "white", textAlign: "center" }}>
            {loading ? "Menyimpan..." : "Create"}
          </Text>
        </View>
      </TouchableOpacity>
    </View>
  );



}


const styles = StyleSheet.create({

  input: {
    height: 40,
    borderWidth: 1,
    borderColor: "#ccc",
    padding: 10,
    marginVertical: 5,
  },
  container: {
    flex: 1, paddingHorizontal: 20, backgroundColor: 'white',
  },
  header: {
    marginTop: 30,
    flexDirection: "row",
    justifyContent: "space-between",
  },
});

export default Post;
