import React, { useState, useEffect } from "react";
import {
  Text,
  View,
  StyleSheet,
  TextInput,
  TouchableOpacity,
  Picker,
} from "react-native";
import COLORS from '../../consts/colors'
import Icon from 'react-native-vector-icons/MaterialIcons';

const Post = ({ navigation }) => {


  const [choosenValue, setChoosenValue] = useState(1);
  const [choosenIndex, setChoosenIndex] = useState(1);
  const [data, setData] = useState([]);


  const [product, setproduct] = useState({
    name: "",
    price: "",
    description: "",
    image: "",
    category_id: "",

  });



  const [loading, setLoading] = useState(false);

  const getAllCategory = async () => {
    try {
      const response = await fetch('http://127.0.0.1:8000/api/Category/');
      const json = await response.json();
      if (response.status == 200) {
        setData(json);

      } else {
      }
    } catch (error) {
      alert("Fout bij aanroepen van api");
      console.error(error);
    }
  };


  // useEffect(() => {
  //   //GET request
  //   fetch('https://jsonplaceholder.typicode.com/posts', {
  //     method: 'GET',
  //     //Request Type
  //   })
  //     .then((response) => response.json())
  //     //If response is in json then in success
  //     .then((responseJson) => {
  //       //Success
  //       //alert(JSON.stringify(responseJson));
  //       // console.log(responseJson);
  //       setData(responseJson);
  //     })
  //     //If response is not in json then in error
  //     .catch((error) => {
  //       //Error
  //       alert(JSON.stringify(error));
  //       console.error(error);
  //     });
  // });


  useEffect(() => {
    getAllCategory();
  }, []);




  const onChangename = (value) => {
    setproduct({ ...product, name: value });
  };

  const onChangeprice = (value) => {
    setproduct({ ...product, price: value });
  };

  const onChangedescription = (value) => {
    setproduct({ ...product, description: value });
  };

  const onChangeimage = (value) => {
    setproduct({ ...product, image: value });
  };


  const saveData = () => {
    setLoading(true);
    var myHeaders = new Headers();


    myHeaders.append("Content-Type", "application/json");

    fetch("http://127.0.0.1:8000/api/Products", {
      method: "POST",
      headers: myHeaders,
      body: JSON.stringify({
        name: product.name,
        price: product.price,
        description: product.description,
        image: product.image,
        category_id: product.category_id,
      }),
    })
      .then((response) => {
        setLoading(false);
        console.log(response);
        response.text();
      })
      // .then((status) => console.log(status))
      .catch((error) => console.log(error));


  };

  return (
    <View style={styles.container}>
      <View>
        <Icon name="arrow-back" size={28} onPress={() => navigation.goBack()} />

        <Text style={{ fontSize: 25, fontWeight: "bold" }}>Welcome to</Text>
        <Text
          style={{ fontSize: 38, color: COLORS.green, fontWeight: "bold" }}
        >
          Yazan And Hamzah Pizza
        </Text>
      </View>
      <TextInput
        placeholder={"Name"}
        onChangeText={(value) => onChangename(value)}
        style={styles.input}
      />
      <TextInput
        placeholder={"Price"}
        onChangeText={(value) => onChangeprice(value)}
        style={styles.input}
      />
      <TextInput
        placeholder={"Description"}
        onChangeText={(value) => onChangedescription(value)}
        style={styles.input}
      />
      <TextInput
        placeholder={"ImageURl"}
        onChangeText={(value) => onChangeimage(value)}
        style={styles.input}
      />
      <Picker
        selectedValue={choosenValue}
        onValueChange={(itemValue, itemIndex) => {

          if (itemValue == 1) { setproduct({ ...product, category_id: 1 }); setproduct({ ...product, category_id: itemValue });}

          setChoosenValue(itemValue);
          setChoosenIndex(itemIndex);

        }}
      >
        {
          data.map(item => {
            return <Picker.Item value={item.id} label={item.name} />
          })
        }
      </Picker>

      <TouchableOpacity onPress={saveData}  >
        <View style={{ backgroundColor: "blue", padding: 10 }}>
          <Text style={{ color: "white", textAlign: "center" }}>
            {loading ? "Menyimpan..." : "Create"}
            {loading == true ? <Icon name="cached" size={28} color="white" /> : null}
            {loading == true ? navigation.navigate("Get") : null}
          </Text>
        </View>
      </TouchableOpacity>
    </View>
  );



}


const styles = StyleSheet.create({

  input: {
    height: 40,
    borderWidth: 1,
    borderColor: "#ccc",
    padding: 10,
    marginVertical: 5,
  },
  container: {
    flex: 1, paddingHorizontal: 20, backgroundColor: 'white',
  },
  header: {
    marginTop: 30,
    flexDirection: "row",
    justifyContent: "space-between",
  },
});

export default Post;
