//import liraries
import React, { Component, useEffect, useState } from 'react';
import { useIsFocused } from '@react-navigation/native';
import {
  View,
  Text,
  StyleSheet,
  FlatList,
  TouchableOpacity,
  Button,
} from 'react-native';
import Icon from "react-native-vector-icons/MaterialIcons";



const Get = ({ navigation }) => {
  const isFocused = useIsFocused();
const [user, setUser] = useState();

  const getUserData = async () => {
    try {
      let response = await fetch('http://127.0.0.1:8000/api/Products/');
      let json = await response.json();
      setUser(json);
    } catch (error) {
      console.error(error);
    }
  };


  useEffect(() => {
  getUserData();
  });



  const renderItem = ({ item }) => {
    return (
      <TouchableOpacity onPress={()=>navigation.navigate('Detail',  item )}>
        <View
          style={{
            borderBottomWidth: 1,
            borderBottomColor: '#ccc',
            padding: 5,
          }}>
          <Text style={{ fontWeight: 'bold' }}>{item.name} </Text>
          <Button title='DEL' onPress={() => navigation.navigate('Delete',item)}></Button>
        </View>
      </TouchableOpacity>
    );
  };

  return (
    <View style={styles.container}>
      <View style={style.header}>
        <View>
          <Text style={{ fontSize: 25, fontWeight: "bold" }}>Welcome to</Text>
          <Text
            style={{ fontSize: 38, color: COLORS.green, fontWeight: "bold" }}
          >
            Yazan And Hamzah Pizza
          </Text>
        </View>

        <View >
          <TouchableOpacity onPress={logout} >
            <Icon name="logout" size={20} color="black" />
          </TouchableOpacity>

          <TouchableOpacity >
            <Icon
              name="favorite"
              size={18}
              color={COLORS.red}
            />
          </TouchableOpacity>
        </View>
      </View>
      <FlatList
        data={user}
        renderItem={renderItem}
        keyExtractor={(item) => item.id}
      />
    </View>
  );
};

// define your styles
const styles = StyleSheet.create({
  container: {
    flex: 1, paddingHorizontal: 20, backgroundColor: white,
  },
  header: {
    marginTop: 30,
    flexDirection: "row",
    justifyContent: "space-between",
  },
});

//make this component available to the app
export default Get;
