import React, { useState } from "react";
import {
  Text,
  View,
  StyleSheet,
  TextInput,
  TouchableOpacity,
} from "react-native";
import Constants from "expo-constants";

 const Post=()=> {
  const [person, setperson] = useState({
    personId: "",
    firstName: "",
    lastName: "",

  });
  address

  const [address, setaddress] = useState({

    addressId: "",
    street: "",
    postalCode: "",
    city: "",
  });
  const [loading, setLoading] = useState(false);

  const onChangepersonId = (value) => {
    setperson({ ...person, personId: value });
  };

  const onChangefirstName = (value) => {
    setperson({ ...person, firstName: value });
  };

  const onChangelastName = (value) => {
    setperson({ ...person, lastName: value });
  };

  const onChangeaddressId = (value) => {
    setaddress({ ...address, addressId: value });
  };
   const onChangestreet = (value) => {
    setaddress({ ...address, street: value });
   };
   const onChangepostalCode = (value) => {
    setaddress({ ...address, postalCode: value });
   };
   const onChangecity = (value) => {
    setaddress({ ...address, city: value });
   };

  const saveData = () => {
    setLoading(true);
    var myHeaders = new Headers();


    myHeaders.append("Content-Type", "application/json");

    fetch("https://myfirstapi.azurewebsites.net/api/person/create", {
      method: "POST",
      headers: myHeaders,
      body: JSON.stringify({
        personId: person.personId,
        firstName: person.firstName,
        lastName: person.lastName,
        address:address,
      }),
    })
      .then((response) => {
        setLoading(false);
        response.text();
      })
      .then((result) => console.log(result))
      .catch((error) => console.log(error));
  };

  return (
    <View style={styles.container}>
      <View>
        <Text style={{ fontSize: 25, fontWeight: "bold" }}>Welcome to</Text>
        <Text
          style={{ fontSize: 38, color: COLORS.green, fontWeight: "bold" }}
        >
          Yazan And Hamzah Pizza
        </Text>
      </View>
      <TextInput
        placeholder={"PersonID"}
        onChangeText={(value) => onChangepersonId(value)}
        style={styles.input}
      />
      <TextInput
        placeholder={"Firstname"}
        onChangeText={(value) => onChangefirstName(value)}
        style={styles.input}
      />
      <TextInput
        placeholder={"LastName"}
        onChangeText={(value) => onChangelastName(value)}
        style={styles.input}
      />
      <TextInput
        placeholder={"addresID"}
        onChangeText={(value) => onChangeaddressId(value)}
        style={styles.input}
      />

      <TextInput
        placeholder={"street"}
        onChangeText={(value) => onChangestreet(value)}
        style={styles.input}
      />
      <TextInput
        placeholder={"Postalcode"}
        onChangeText={(value) => onChangepostalCode(value)}
        style={styles.input}
      />
      <TextInput
        placeholder={"City"}
        onChangeText={(value) => onChangecity(value)}
        style={styles.input}
      />

      <TouchableOpacity onPress={saveData} >
        <View style={{ backgroundColor: "blue", padding: 10 }}>
          <Text style={{ color: "white", textAlign: "center" }}>
            {loading ? "Menyimpan..." : "Create"}
          </Text>
        </View>
      </TouchableOpacity>
    </View>
  );



}


const styles = StyleSheet.create({

  input: {
    height: 40,
    borderWidth: 1,
    borderColor: "#ccc",
    padding: 10,
    marginVertical: 5,
  },
  container: {
    flex: 1, paddingHorizontal: 20, backgroundColor: 'white',
  },
  header: {
    marginTop: 30,
    flexDirection: "row",
    justifyContent: "space-between",
  },
});

export default Post;
