import React, { useState, useEffect } from 'react';
import {
    View,
    StyleSheet,
    Image,
    Text,
    TextInput,
    Alert,
    Button,
} from 'react-native';




const Login=({ navigation })=> {

    const [User, setUser] = useState([ ]);
    const [Email, seEmail] = useState('');
    const [Password, setPassword] = useState('');
    const getUser= async (Email) => {
        try {
          const response = await fetch('http://127.0.0.1:8000/api/User?email=' + Email.toString() );
          const json = await response.json();
          if (response.status == 200) {

            setUser(json);

          } else {
          }
        } catch (error) {
          alert("Fout bij aanroepen van api");
          console.error(error);
        }
      };

      const login = () => {

        if(Email==''){  
            Alert.alert("Email is leeg");
        };
        if(Password==''){
            Alert.alert("Password is leeg");
        };
        if(Email!='' && Password!=''){
            getUser(Email);
            if(User.length==0){
                Alert.alert("Email is niet gevonden");
            }else{
                if(User[0].password==Password){
                    navigation.navigate('Home', {
                        user: User[0]
                    });
                }else{
                    Alert.alert("Password is niet correct");
                }
            }
        }
        };



    const newLocal = <Button
        title='Login'
        color='#1eb900'
        onPress={() =>  navigation.navigate("Home")}>
        </Button>;


    return (
        <View style={styles.body} >

            <Text style={styles.text}>
                Async Storage
            </Text>
            <TextInput
                style={styles.input}
                placeholder='Enter your name'
                onChangeText={(value) => seEmail(value)}
            />
            <TextInput
                style={styles.input}
                placeholder='Enter your age'
                onChangeText={(value) => setPassword(value)}
            />
            {newLocal}

        </View>
    )
};
export default Login;

const styles = StyleSheet.create({
    body: {
        flex: 1,
        alignItems: 'center',
        backgroundColor: '#0080ff',
    },
    logo: {
        width: 100,
        height: 100,
        margin: 20,
    },
    text: {
        fontSize: 30,
        color: '#ffffff',
        marginBottom: 130,
    },
    input: {
        width: 300,
        borderWidth: 1,
        borderColor: '#555',
        borderRadius: 10,
        backgroundColor: '#ffffff',
        textAlign: 'center',
        fontSize: 20,
        marginBottom: 10,
    }
});
