import {
  View,
  StyleSheet,
  Image,
  Text,
  TextInput,
  Alert,
  Button,
} from "react-native";
import { AppState } from "react-native-web";

const Login = ({ navigation }) => {
  const [User, setUser] = useState([]);
  const [Email, seEmail] = useState("");
  const [Password, setPassword] = useState("");
  const [EmailDB, seEmailDB] = useState("");
  const [PasswordDB, setPassworDB] = useState("");
  const [rool, setrool] = useState("");

  const getUser = async () => {
    try {
      const response = await fetch(
        "http://127.0.0.1:8000/api/User?email=" + Email.toString()
      );
      const json = await response.json();
      if (response.status == 200) {
        console.log(response.status);
        AppState.setUser(json);
      } else {
      }
    } catch (error) {
      alert("Fout bij aanroepen van api");
      console.error(error);
    }
  };

  const login = () => {
    if (Email == "") {
      alert("Email is leeg");
    }
    if (Password == "") {
      alert("Password is leeg");
    }
let user = User.filter((item) => item.email == Email);
    if (Email != "" && Password != "") {
      getUser();
      if (Email == EmailDB && Password == PasswordDB) {
        if (rool == "Admin") {
          navigation.navigate("Admin");
        } else {
          navigation.navigate("Home", {});
        }
      }
    }
  };

  // const yy =  (e) => {
  //   console.log(e);
  //  useEffect(() => {
  //   seEmailDB(e);
  //   }, []);
  //  console.log(EmailDB);
  // };

  return (
    <View style={styles.body}>
      <Text style={styles.text}>Async Storage</Text>
      <TextInput
        style={styles.input}
        placeholder="Enter your name"
        onChangeText={(value) => seEmail(value)}
      />

      <TextInput
        style={styles.input}
        placeholder="Enter your age"
        onChangeText={(value) => setPassword(value)}
      />

      <>
        <Button title="Login" color="#1eb900" onPress={() => login()}></Button>
        {/* <Button title="rrr" color="#1eb900" onPress={() => yy()}></Button> */}
      </>
    </View>
  );
};
export default Login;

const styles = StyleSheet.create({
  body: {
    flex: 1,
    alignItems: "center",
    backgroundColor: "#0080ff",
  },
  logo: {
