import React, { useState, useEffect } from "react";
import {
  View,
  StyleSheet,
  Image,
  Text,
  TextInput,
  Alert,
  Button,
} from "react-native";

const Login = ({ navigation }) => {
  const [User, setUser] = useState([]);
  const [Email, seEmail] = useState("");
  const [Password, setPassword] = useState("");
  const getUser = async () => {
    try {
      const response = await fetch("http://127.0.0.1:8000/api/User");
      const json = await response.json();
      if (response.status == 200) {
        console.log(response.status);
        await setUser(json);
      } else {
      }
    } catch (error) {
      alert("Fout bij aanroepen van api");
      console.error(error);
    }
  };

  const [EmailDB, seEmailDB] = useState("");
  const login = () => {
    if (Email == "") {
      alert("Email is leeg");
    }
    if (Password == "") {
      alert("Password is leeg");
    }
    if (Email != "" && Password != "") {
      for (let i = 0; i < User.length; i++) {
        console.log(User);
        if (User[i].email == Email) {
            yy(User[i].email);
          if (User[i].password == Password) {
            if (User[i].rool == "Admin") {
              navigation.navigate("Admin");
            } else {
              navigation.navigate("Home", {
                user: User[i],
              });
            }
            i = User.length - 1;
          } else {
            alert("Password is niet correct");
            console.log(EmailDB);
          }
        } else {
          if (i == User.length - 1) {
            console.log(EmailDB);
            alert("Email is niet gevonden");
          }
        }
      }
    }
  };
  const yy = () => {
    console.log(User);
  };

  useEffect(() => {
    getUser();
    console.log(User);
  }, []);

  return (
    <View style={styles.body}>
      <Text style={styles.text}>Async Storage</Text>
      <TextInput
        style={styles.input}
        placeholder="Enter your name"
        onChangeText={(value) => seEmail(value)}
      />

      <TextInput
        style={styles.input}
        placeholder="Enter your age"
        onChangeText={(value) => setPassword(value)}
      />

      <>
        <Button title="Login" color="#1eb900" onPress={() => login()}></Button>
        {/* <Button title="rrr" color="#1eb900" onPress={() => yy()}></Button> */}
      </>
    </View>
  );
};
export default Login;

const styles = StyleSheet.create({
  body: {
    flex: 1,
    alignItems: "center",
    backgroundColor: "#0080ff",
  },
  logo: {
    width: 100,
    height: 100,
    margin: 20,
  },
  text: {
    fontSize: 30,
    color: "#ffffff",
    marginBottom: 130,
  },
  input: {
    width: 300,
    borderWidth: 1,
    borderColor: "#555",
    borderRadius: 10,
    backgroundColor: "#ffffff",
    textAlign: "center",
    fontSize: 20,
    marginBottom: 10,
  },
});
